import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(0.5),
    },
  },
  paper: {
    padding: theme.spacing(1),
    maxWidth: '500px',
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  fileInput: {
    width: '97%',
    margin: '5px 0',
  },
  buttonSubmit: {
    marginBottom: 5,
  },
}));
