import React from "react";
import {Container,Grow,Grid,Paper,AppBar,TextField,Button} from '@material-ui/core';
import { useDispatch } from "react-redux";
import { useHistory,useLocation } from "react-router-dom";
import ChipInput from 'material-ui-chip-input';
import { useState,useEffect } from "react";
import {getPosts,getPostsBySearch} from "../../actions/posts";
import Posts from "../Posts/Posts";
import Form from "../Form/Form";
import Pagination from "../pagination";
import { useParams } from "react-router-dom";



import useStyles from './styles';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

const Home = () => {

      const [currentId, setCurrentId] = useState(0);
      const dispatch = useDispatch();
    const query = useQuery();
    const history = useHistory();
    const {page  } = useParams();
    const classes = useStyles();
  const [search, setSearch] = useState('');
  const [tags, setTags] = useState([]);


      const searchPost = () => {
        if(search.trim()||tags){
          dispatch(getPostsBySearch({search, tags: tags.join(',')}));
        }else{
          history.push(`/posts/search?searchQuery=${search || 'none'}&tags=${tags.join(',')}` );
        }
      };

      const handleKeyPress = (e) => {
        if(e.keyCode === 13){
          searchPost();
        }
      };



useEffect(() => {
   dispatch(getPosts(page));
}, [dispatch, page]);
      return (
            <Grow in>
        <Container maxWidth="xl">
          <Grid container justifyContent="space-between" alignItems="stretch" spacing={3} className={classes.gridContainer}>
            <Grid item xs={12} sm={6} md={9}>
              <Posts setCurrentId={setCurrentId}/>
              </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <AppBar className={classes.appBarSearch} position="static" color="inherit">
                <TextField name ="search" variant="outlined" label="Search Memories" onKeyPress={handleKeyPress} fullWidth value={search} onChange={(e)=>setSearch(e.target.value)}/>
                  <ChipInput style={{margin:'10px 0'}} value={tags} onAdd={(chip)=>setTags([...tags,chip])} onDelete={(chipToDelete)=> setTags(tags.filter((tag)=> tag !== chipToDelete))} label="Search Tags" variant="outlined"/>
                  <Button onClick={searchPost} className={classes.searchButton} variant="contained" color="primary">Search</Button>
                  </AppBar>
              <Form currentId={currentId} setCurrentId={setCurrentId}/>
              <Paper elevation={6}>
                <Pagination  page={page}/>
              </Paper>
              </Grid>
          </Grid>
        </Container>
      </Grow>

      );
}

export default Home;
